import React, { useState } from "react";

const [list, updateList] = useState(hits);
    const Hits = ({ hits }) => {


  
      function handleRemove(id) {
        // remove item
        alert(id);

        updateList(hits.filter(item => item !== id));

   
      }

      console.log(updateList)  ;
      return (
        <div>
          <center><h1>Hits List</h1></center>
          {hits.map((hit,key) => (
            <div class="card"  key={key}>
              <div class="card-body">
              <h6 class="card-subtitle mb-2 text-muted">  
              
              <button type="button" onClick={() => handleRemove(key)}>
            Remove
          </button>
           Titulo:: { hit.title}</h6>
              <h6 class="card-subtitle mb-2 text-muted">  Fecha:: { hit.created_at}</h6>
              <h6 class="card-subtitle mb-2 text-muted">  Author::    {hit.author }</h6>
              <h6 class="card-subtitle mb-2 text-muted">  -------------------------------------</h6>
              </div>
            </div>
          ))}
        </div>
      )
    };

    export default Hits
