import React, {Component} from 'react';
import Hits from './components/contacts';

class App extends Component {
    render() {
        return (
            <Hits hits={this.state.hits} removeItem={this.removeItem}/>
        )
    }
    constructor(props) {
        super(props);
        this.state = { hits: [], text: '' };
        this.removeItem = this.removeItem.bind(this)
      }

    removeItem(index) {
        const hits = this.state.todos.filter((hits, todoIndex) => {
          return todoIndex !== index
        })
        this.setState({ hits })
      }
    state = {
        hits: []
    };

    componentDidMount() {
        fetch('https://hn.algolia.com/api/v1/search_by_date')
            .then(res => res.json())
            .then((data) => {
                console.log(data.hits)
                this.setState({hits: data.hits })
            })
            .catch(console.log)
    }
}

export default App;
