import React, {Component} from 'react';
import Hits from './components/contacts';

class App extends Component {
    render() {
        return (
            this.state.hits
        )
    }

    state = {
        hits: []
    };

    componentDidMount() {
        fetch('https://hn.algolia.com/api/v1/search_by_date')
            .then(res => res.json())
